import './App.css';
//import { getProducts } from './components/GetData';
import { useEffect, useState } from 'react';
import { ProductImg, ProductContainer, ProductContainerP } from './components/styled/StyledProductsPage';

function App() {
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setIsLoading(true);
    const getProducts = async () => {
      await fetch('https://fakestoreapi.com/products')
            .then(res=>res.json())
            .then(json=>setProducts(json));
    }
    getProducts();
    setIsLoading(false);
  }, [products.length])

  return (
    <div className="App">
      { products.length > 0 ? (
        <ProductContainer type="div">
          <ProductContainerP>{products[0].title}</ProductContainerP>
          <p>{products[0].description}</p>
          <ProductImg type="img" src={products[0].image} />
        </ProductContainer>
      ) : (
        <p>Loading</p>
      )}

    </div>
  );
}

export default App;
