import { createSlice } from "@reduxjs/toolkit"

export const cartSlice = createSlice({
    name: 'cart',
    initialState: {
        productCart: [],
    },
    reducers: {
        appendToCart: (state, action) => {
            let tempCart = [...state.productCart];
            for(var i=0; i<tempCart.length; i++){
                if(action.payload.id == tempCart[i].action.payload.id){
                    tempCart[i].quantity = tempCart[i].quantity + quantity;
                    state.productCart = tempCart;
                    return;
                }
            }
            tempCart.push({
                product: action.payload.product,
                quantity: quantity
            })
            state.productCart = tempCart;
        },
        deleteFromCart: (state, action) => {
            let tempCart = [...state.productCart];
            console.log(action.payload);
            tempCart.splice(action.payload, 1);
            state.productCart = tempCart;
        },
        clearCart: (state) => {
            state.productCart = [];
        }
    }
});

export const { appendToCart, deleteFromCart, clearCart } = cartSlice.actions;
export const selectCart = (state) => state.cart.cart;
export default cartSlice.reducer;
