import { useContext } from "react";
import { useState } from "react/cjs/react.development";
import { ProductContext } from "../contexts/ProductContext";
import { ProductListContainer } from "../styled/StyledProductsPage";
import ProductCard from "./ProductCard";

const ProductList = (props) => {
    const { products } = useContext(ProductContext);

    const [productList, setProductList] = useState([...products]);
    const [category, setCategory] = useState("none");


    return (
        <>
            <div>
                <h2>Filter By Category</h2>
                <form>
                    <input type="radio" name="category" id="none" value="none" onClick={(e) => setCategory(e.target.value)} />
                    <label for="none">None</label>

                    <input type="radio" name="category" id="men" value="men's clothing" onClick={(e) => setCategory(e.target.value)} />
                    <label for="men">Men's Clothing</label>

                    <input type="radio" name="category" id="women" value="women's clothing" onClick={(e) => setCategory(e.target.value)} />
                    <label for="women">Women's Clothing</label>

                    <input type="radio" name="category" id="jewel" value="jewelery" onClick={(e) => setCategory(e.target.value)} />
                    <label for="jewel">Jewelery</label>

                    <input type="radio" name="category" id="electronics" value="electronics" onClick={(e) => setCategory(e.target.value)} />
                    <label for="electronics">Electronics</label>

                </form>

                <h2>Order By Price</h2>
                <form>
                    <input type="radio" name="price" id="highest" />
                    <label for="highest">Highest to Lowest</label>

                    <input type="radio" name="price" id="lowest" />
                    <label for="lowest">Lowest to Highest</label>
                </form>

            </div>
            { products.length > 0 ? (
                <ProductListContainer type="div">
                {products.map(product =>
                    <ProductCard 
                        title={product.title}
                        price={product.price}
                        imageUrl={product.image}
                        product={product} />
                )}
                </ProductListContainer>
            ) : (
                <p>Loading</p>
            )}
        </>
    )
}

export default ProductList;
