import './App.css';
import { getProducts } from './components/GetData';
import { useEffect, useState } from 'react';

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    let data = getProducts();
    setProducts(data);
  }, [products])

  return (
    <div className="App">
      {products[0].title}
      {products[0].description}
      <img src={products[0].image} ></img>
    </div>
  );
}

export default App;
