import { useContext } from "react";
import { Outlet } from "react-router-dom";
import { CartContext } from "./contexts/CartContext";
import { Header, HeaderH1, HeaderLinks, HeaderP, HeaderTitle } from "./styled/StyledAppHeader";
import { Link } from "react-router-dom";
import App from "../App";

const AppHeader = (props) => {
    const { productCart } = useContext(CartContext);

    return (
        <>
            <Header>
                <HeaderTitle>
                    <HeaderH1>Fake Store</HeaderH1>
                </HeaderTitle>
                <HeaderLinks>
                    <Link to="/products/main" style={{ textDecoration: 'none' }}><HeaderP>Home</HeaderP></Link>
                    <Link to="/products/cart" style={{ textDecoration: 'none' }}><HeaderP>Cart Items: {productCart?.length}</HeaderP></Link>
                    <Link to="/products/checkout" style={{ textDecoration: 'none' }}><HeaderP>Checkout</HeaderP></Link>
                </HeaderLinks>
            </Header>
            <App />
            <Outlet />
        </>

    )
}

export default AppHeader;
