import { useContext } from "react"
import { ProductContext } from "../contexts/ProductContext"
import { ProductContainer, ProductImg, ProductContainerH1, ProductContainerP } from "../styled/StyledProductsPage";


const ProductDetails = ({index}, props) => {
    const { products } = useContext(ProductContext);

    return (
        <ProductContainer type="div">
              <ProductImg type="img" src={products[index]?.image} />
              <ProductContainerH1>{products[index]?.title}</ProductContainerH1>
              <ProductContainerP>{products[index]?.price}</ProductContainerP>   
              <ProductContainerP>{products[index]?.category}</ProductContainerP>
              <ProductContainerP>{products[index]?.description}</ProductContainerP>
        </ProductContainer>    
    )
}

export default ProductDetails;
