import styled from "styled-components";

export const CheckoutCardList = styled.div`
    display:flex;
    flex-direction:column;
`

export const CheckoutCard = styled.div`
    display:flex;
    margin: 20px;
    padding: 10px;
    width:50%;
`

export const CheckoutCardDescripBox = styled.div`
    display:flex;
    justify-content:left;
    flex-wrap:nowrap;
    align-items:center;
`

export const CheckoutCardDescrip = styled.div`
    margin-left:20px;
`

export const CheckoutCardButtonBox = styled.div`
    display:flex;
    margin-left:auto;
    align-items:center;
`
export const CheckoutCardButton = styled.button`
    color:white;
    background-color:grey;
    border-radius:50%;
    border:none;
    font-size:20px;
`
