import './App.css';
//import { getProducts } from './components/GetData';
import { useEffect, useState } from 'react';
import { ProductImg, ProductContainer, ProductContainerP, ProductList } from './components/styled/StyledProductsPage';

function App() {
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setIsLoading(true);
    const getProducts = async () => {
      await fetch('https://fakestoreapi.com/products')
            .then(res=>res.json())
            .then(json=>setProducts(json));
    }
    getProducts();
    setIsLoading(false);
  }, [products.length])

  return (
    <div className="App">
      { products.length > 0 ? (
        <ProductList type="div">
         {products.map(product =>
           <ProductContainer type="div">

              <p>{product.title}</p>
              <ProductContainerP>{product.description}</ProductContainerP>
              <ProductImg type="img" src={product.image} />   
            </ProductContainer>       
          )}
        </ProductList>
      ) : (
        <p>Loading</p>
      )}

    </div>
  );
}

export default App;
