import styled from "styled-components";

export const ProductDetailsContainer = styled.div`
    width:50%;
    display:flex;
    flex-wrap:wrap;
    align-items:center;
    flex-direction:column;
    margin:auto;
`
export const ProductDetailsImg = styled.img`
    display:flex;
    justify-content:left;
    width:20%;

`
